import { Button, Popconfirm, message } from "antd";
import axios from "axios";
import { ObjectId } from "mongoose";
import Image from "next/image";
import { useRouter } from "next/navigation";

interface documentType {
    _id: string
    title: string;
    ISBN: number;
    author: string;
    publication_date: string;
    genre: string;
    description: string;
    publisher: string;
    reservedDocuments: number,
    loanedDocuments: number,
    loanable: number,
}

export default function ReservationElement(props: any) {

    const doc = props?.doc;

    const router = useRouter();

    const onCancel = async () => {
        try{
            axios.post("/api/documentDB/cancel-reservation", {reservationId: doc?._id}).then((response) => {
                message.success(response.data.message);
            });
            location.reload();
        }
        catch (error: any) {
            message.error(error.response.data.message);
        }
    }

    const onLoan = async () => {
        try{
            axios.post("/api/documentDB/loan-document", {reservationId: doc?._id}).then((response) => {
                message.success(response.data.message);
            });
            location.reload();
        }
        catch (error: any) {
            message.error(error.response.data.message);
        }
    }

    const MILLISECONDSPERDAY = 24*60*60*1000;

    const RESERVATIONDURATION = 7;

    const timeElapsed = Math.round((Date.now() - +(new Date(doc?.startDate)))/MILLISECONDSPERDAY);


    return (
        <div
        key={doc?._id.toString()}
        className="rounded bg-[#F9EFE5] grow p-2 flex flex-row my-4">
            <div className="shrink-0 relative w-32 h-32">
                <Image
                    fill={true}
                    src={"data:image/png;base64,"}
                    alt="Document Image"/>
            </div>
            <div className="px-5 grow">
                <p className="font-semibold text-xl mb-0.5 mt-2">{doc?.title}</p>
                <p className="mt-0 text-slate-400 text-xs">{doc?.publisher + ", " + doc?.publication_date?.substring(0,4)}</p>
                <p className="italic text-blue-900 font-medium mt-1.5">{doc?.author}</p>
                <p className="font-semibold text-base mb-0.5 mt-2.5">User Id: {doc?.userId}</p>
            </div>
            <div className="mr-4 mt-1 flex flex-col gap-2">
                <p className="text-slate-400 text-xs m-0">Reservation</p>
                <p className="text-slate-400 text-xs m-0">Days left: {(RESERVATIONDURATION - timeElapsed)}</p>
                <Popconfirm
                    title="Cancel reservation"
                    description="Are you sure to cancel the reservation?"
                    onConfirm={onCancel}
                    okText="Yes"
                    cancelText="No"
                >
                    <Button danger>Cancel reservation</Button>
                </Popconfirm>
                <Popconfirm
                    title="Confirm loan"
                    description="Document will be signed as loaned"
                    onConfirm={onLoan}
                    okText="Yes"
                    cancelText="No"
                >
                    <Button>Confirm Loan</Button>
                </Popconfirm>
                <Button href={"/catalogo-bibliografico/" + doc.documentId}>
                    Go to document page
                </Button>
            </div>
        </div>
    )

    return (
        <p>ERROR LOADING DOCUMENT</p>
    )
}